mapper.select:
select sm.id, sm.manual_name, sm.real_name,sm.manual_type, dm.name, sm.manual_format, sm.manual_url, sm.status,dv.name AS versionName from service_manual as sm LEFT JOIN dict_manual dm ON sm.manual_type=dm.id LEFT JOIN dict_version dv ON sm.version_type=dv.id where 1=1
AND sm.manual_name LIKE CONCAT('%',#{manualName,jdbcType=VARCHAR}, '%')
AND sm.manual_type =#{manualType,jdbcType=VARCHAR}
AND sm.version_type =#{versionType,jdbcType=INTEGER}

select id, manual_name,manual_url from service_manual WHERE status=1
AND manual_name LIKE CONCAT('%',#{manualName,jdbcType=VARCHAR}, '%')
AND manual_type =#{manualType,jdbcType=VARCHAR}
AND version_type =#{versionType,jdbcType=INTEGER}

mapper.insert:
insert into service_manual (id, manual_name, real_name, 
  manual_type, manual_format, manual_url, version_type ,
  status, gmt_create
  )
values (#{id,jdbcType=INTEGER}, #{manualName,jdbcType=VARCHAR}, #{realName,jdbcType=VARCHAR}, 
  #{manualType,jdbcType=TINYINT}, #{manualFormat,jdbcType=VARCHAR}, #{manualUrl,jdbcType=VARCHAR},  #{versionType,jdbcType=BIT},
  #{status,jdbcType=BIT}, #{gmtCreate,jdbcType=TIMESTAMP}
  )
