mapper.select:
select wm.id, wm.parent_id, wm.menu_type, wm.menu_name, wm.menu_url, wm.description, wm.order_num, wm.manual_type, wm.version_type, wm.status,dm.`name` AS manualName ,dv.`name` AS versionName from wechat_menu wm LEFT JOIN dict_manual dm ON wm.manual_type=dm.id LEFT JOIN dict_version dv ON wm.version_type=dv.id WHERE wm.menu_type=1 AND wm.parent_id=0
AND wm.version_type =#{versionType,jdbcType=INTEGER}
ORDER BY wm.order_num

select wm.id, wm.parent_id, wm.menu_type, wm.menu_name, wm.menu_url, wm.description, wm.order_num, wm.manual_type, wm.version_type, wm.status,dm.`name` AS manualName ,dv.`name` AS versionName from wechat_menu wm LEFT JOIN dict_manual dm ON wm.manual_type=dm.id LEFT JOIN dict_version dv ON wm.version_type=dv.id WHERE wm.parent_id=#{parentId,jdbcType=INTEGER}
AND wm.menu_type=#{menuType,jdbcType=INTEGER}
AND wm.version_type =#{versionType,jdbcType=INTEGER} AND wm.status=1 ORDER BY wm.order_num

mapper.insert:
insert into wechat_menu (id, parent_id, menu_type,
  menu_name, menu_url, description,
  order_num, manual_type, version_type,
  status, gmt_create, gmt_modified
  )
values (#{id,jdbcType=INTEGER}, #{parentId,jdbcType=INTEGER}, #{menuType,jdbcType=INTEGER},
  #{menuName,jdbcType=VARCHAR}, #{menuUrl,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR},
  #{orderNum,jdbcType=INTEGER}, #{manualType,jdbcType=INTEGER}, #{versionType,jdbcType=INTEGER},
  #{status,jdbcType=BIT}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}
  )
